scriptSource.command:
// PURGE OLD PRERELEASE JOBS

import hudson.model.AbstractProject
import java.util.Calendar
import java.util.regex.Matcher
import java.util.regex.Pattern
import jenkins.model.Jenkins

c = Calendar.getInstance();
c.add(Calendar.DAY_OF_MONTH, -30);

pattern = Pattern.compile("prerelease.*")
for (p in Jenkins.instance.getAllItems(AbstractProject)) {
	if (!pattern.matcher(p.name).matches()) continue
	// ignore queued jobs
	if (p.queueItem) continue
	b = p.lastBuild
	// ignore recent jobs
	if(b && b.timestamp > c) continue
	// delete old jobs
	println(p.name)
	p.delete()
}
